import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Row, Col } from 'react-bootstrap';

const LoginRes = () => {
    const [login, setLogin] = useState({ username: '', password: '' });
    const [register, setRegister] = useState({ firstName: '', lastName: '', username: '', password: '' });
    const [profileImage, setProfileImage] = useState(null);

    const handleLogin = event => {

    }

    const handleRegister = event => {

    }

    const handleProfileImageOnSelect = event => {
        const file = event.target.files[0];
        if (file !== null) {
            setProfileImage(URL.createObjectURL(event.target.files[0]));
        }
    }

    useEffect(() => {
        console.log(profileImage);
    }, [profileImage]);

    return (
        <Row>
            <Col className="p-5 heightFull align-items-center" md={5}>
                <div className="w-100 mb-5">
                    <h1 className="font-weight-bold display-4">Chazzy.</h1>
                    <p className="text-secondary">A time messaging app.</p>
                    <form className="my-5">
                        <input className="form-control shadow-input" placeholder="username" type="text" />
                        <input className="form-control shadow-input mt-2" placeholder="password" type="password" />
                        <button className="btn btn-secondary shadow-button mt-5" type="submit">Sign In</button>
                    </form>
                </div>
            </Col>
            <Col className="p-5 heightFull align-items-center" md={7}>
                <div className="w-100 mb-5 text-center">
                    <h1 className="font-weight-bold">Register</h1>
                    <p className="text-secondary">Don't have an account? Sign Up.</p>
                    <form className="my-5" encType="multipart/form-data">>
                        <div>
                            <label>Select your profile image!</label>
                            <img width="100" height="100" className="rounded-circle" accept="image/*" src={profileImage ? profileImage : ''} alt="" />
                            <input onChange={handleProfileImageOnSelect} className="border-none" type="file" required />
                        </div>
                        <input className="form-control shadow-input my-2" placeholder="First Name" type="text" required />
                        <input className="form-control shadow-input my-2" placeholder="Last Name" type="text" required />
                        <input className="form-control shadow-input my-2" placeholder="@username" type="text" required />
                        <input className="form-control shadow-input mt-2" placeholder="password" type="password" required />
                        <button style={{ backgroundColor: 'rgb(228, 228, 228)' }} className="btn shadow-button mt-5" type="submit">Sign Up</button>
                    </form>
                </div>
            </Col>
            <p className="copyright-label m-0 text-secondary">&#9400;QuangNguyen 2020</p>
        </Row>
    );
}

export default LoginRes;
