import React, { useState } from 'react';
import axios from 'axios';
import { Row, Col, Modal, Button } from 'react-bootstrap';

const LoginRes = () => {
    const [login, setLogin] = useState({ username: '', password: '' });
    const [register, setRegister] = useState({ firstName: '', lastName: '', username: '', password: '', confirmPassword: '' });
    const [profileImage, setProfileImage] = useState(null);
    const [errors, setErrors] = useState([]);

    const [showErrors, setShowErrors] = useState(false);
    const handleClose = () => setShowErrors(false);
    const handleShow = () => setShowErrors(true);

    const handleLogin = event => {

    }

    const handleRegister = event => {
        event.preventDefault();
        axios.post('http://localhost:8000/api/users/register', register)
            .catch(err => { })
            .then(res => {
                if ('msg' in res.data) {
                    if (res.data['msg'] === 'success') {
                        // TODO
                        // Log user in
                        const jwt = require("jsonwebtoken");
                    }
                } else {
                    let messages = Object.values(res.data.errors).map(val => val.message);
                    messages.push(res.data.name);
                    setErrors(messages);
                    handleShow();
                }
            })
    }

    const profileImageOnSelect = event => {
        const file = event.target.files[0];
        if (file !== null) {
            setProfileImage(file);
        }
    }

    const loginFieldOnChange = event => setLogin({ ...login, [event.target.name]: event.target.value });
    const registerFieldOnChange = event => setRegister({ ...register, [event.target.name]: event.target.value });

    return (
        <Row>
            <Col className="p-5 heightFull align-items-center tracking-in-expand-fwd" md={5}>
                <div className="w-75 mb-5">
                    <h1 className="font-weight-bold display-4">Chazzy.</h1>
                    <p className="text-secondary">A simple timed messaging app.</p>
                    <form className="my-5">
                        <input onChange={loginFieldOnChange} className="form-control shadow-input login-shadow" name="username" placeholder="username" type="text" />
                        <input onChange={loginFieldOnChange} className="form-control shadow-input login-shadow mt-2" name="password" placeholder="password" type="password" />
                        <button className="btn btn-secondary shadow-button mt-5" type="submit">Sign In</button>
                    </form>
                </div>
            </Col>
            <Col className="p-5 heightFull align-items-center tracking-in-contract" md={7}>
                <div className="w-75 mb-5 text-center">
                    <h1 className="font-weight-bold">Register</h1>
                    <p className="text-secondary">Don't have an account? Sign Up.</p>
                    <form className="my-5" onSubmit={handleRegister}>
                        {/* <ul className="list-inline text-secondary my-3">
                            <li className="list-inline-item align-middle mx-3">
                                <img width="100" height="100" className="rounded-circle" accept="image/*" src={profileImage ? URL.createObjectURL(profileImage) : ''} alt="" />
                            </li>
                            <li className="list-inline-item align-middle mx-3">
                                <label>Select your profile image!</label><br />
                                <input onChange={profileImageOnSelect} className="border-none" type="file" required />
                            </li>
                        </ul> */}
                        <Row>
                            <Col>
                                <input onChange={registerFieldOnChange} className="form-control shadow-input my-2" name="firstName" placeholder="First Name" type="text" required />

                            </Col>
                        </Row>
                        <input onChange={registerFieldOnChange} className="form-control shadow-input my-2" name="lastName" placeholder="Last Name" type="text" required />
                        <input onChange={registerFieldOnChange} className="form-control shadow-input my-2" name="username" placeholder="username" type="text" required />
                        <input onChange={registerFieldOnChange} className="form-control shadow-input mt-2" name="password" placeholder="password" type="password" required />
                        <input onChange={registerFieldOnChange} className="form-control shadow-input mt-2" name="confirmPassword" placeholder="Confirm Password" type="password" required />

                        <>
                            <button style={{ backgroundColor: 'rgb(225, 225, 225)' }} className="btn shadow-button mt-5" type="submit">Sign Up</button>
                            <Modal show={showErrors} onHide={handleClose} className="no-border" centered>
                                <Modal.Header closeButton className="no-border">
                                    <Modal.Title className="font-weight-bold">Ooops! Something went wrong. :(</Modal.Title>
                                </Modal.Header>
                                <Modal.Body className="border-none">
                                    <ul className="list-inline text-danger">{errors.map((err, idx) => <li key={idx}>- {err}</li>)}</ul>
                                </Modal.Body>
                                <Modal.Footer className="no-border">
                                    <Button variant="light" onClick={handleClose}>Got It!</Button>
                                </Modal.Footer>
                            </Modal>
                        </>
                    </form>
                </div>
            </Col>
            <p className="copyright-label m-0 text-secondary">&#9400;QuangNguyen 2020</p>
        </Row >
    );
}

export default LoginRes;
