import React, { useContext, useState, useRef } from 'react';
import Context from '../Context';
import io from 'socket.io-client';
import axios from 'axios';
import UserProfilePreview from './UserProfilePreview';

const ChatArea = ({ userWhomAmChattingWith, passedInMessages }) => {
    const { loggedInUser } = useContext(Context);
    const [messageText, setMessageText] = useState('');
    const messageInputRef = useRef();
    const [socket] = useState(() => io(':8000'));

    const formOnSubmit = event => {
        event.preventDefault();
        const newMessage = { message: messageText, senderID: loggedInUser._id, receiverID: userWhomAmChattingWith._id };
        axios.post('http://localhost:8000/api/messages/send', newMessage, { withCredentials: true })
            .then(res => {
                messageInputRef.current.value = '';
                socket.emit('new-message', res.data);
            })
            .catch(err => console.log(err));
    }

    const isMyMessage = message => message.senderID === loggedInUser._id;

    return (
        <div>
            <ul className="list-inline text-center mx-1">
                <li>
                    <UserProfilePreview user={userWhomAmChattingWith} />
                </li>
                {passedInMessages ? passedInMessages.map(message => (
                    <li key={message._id} className={isMyMessage(message) ? 'text-right' : 'text-left'}>
                        <p className="mx-2">
                            {isMyMessage(message) ? message.message : <></>}
                            <span className={`p-2 rounded ${isMyMessage(message) ? 'ml-2 bg-secondary text-light' : 'mr-2 bg-light'}`}>@{isMyMessage(message) ? 'me' : userWhomAmChattingWith.username}</span>
                            {isMyMessage(message) ? <></> : message.message}
                        </p>
                    </li>
                )) : <></>}
            </ul>
            <footer style={{ position: 'absolute', bottom: '24px', left: '24px', right: '0', scrollBehavior: 'sticky' }} className="text-center p-0">
                <form onSubmit={formOnSubmit} className="form-row w-100 text-center m-0">
                    <input ref={messageInputRef} onChange={event => setMessageText(event.target.value)} type="text" className="form-control bg-light col-6 p-4 mx-1 border-0" placeholder="Type in your message here" />
                    {messageText.length > 0 ? <input type="submit" className="btn btn-secondary col-2 mr-1" value="Send" /> : <input type="submit" className="btn btn-secondary col-2 mr-1" value="Send" disabled />}
                    {messageText.length > 0 ? <button className="btn btn-success col-2 mr-1">Now</button> : <button className="btn btn-success col-2 mr-1" disabled>Now</button>}
                </form>
            </footer>
        </div>
    );
}

export default ChatArea;
