import React, { useState, useEffect, useContext } from 'react';
import { navigate } from '@reach/router';
import axios from 'axios';
import Context from '../Context';

// Bootstrap & Images
import { Navbar, Nav, Row, Col } from 'react-bootstrap';
import styles from './Dashboard.module.css';
import '../App.css';
import Grey from '../grey.jpg';

const UserProfilePreview = ({ user, onClick }) => {
    return (
        <button style={{ background: 'none', border: '0px solid white', outlineStyle: 'none' }} onClick={onClick}>
            <img width="90" height="90" className="rounded-circle" src={Grey} alt="" /><br />
            <p className="mt-2">@{user ? user.username : 'username'}</p>
        </button>
    );
}

const ChatArea = ({ userWhomAmChattingWith }) => {
    const [messageText, setMessageText] = useState('');
    const { loggedInUser } = useContext(Context);

    const formOnSubmit = event => {
        event.preventDefault();
        const newMessage = { message: messageText, senderID: loggedInUser._id, receiverID: userWhomAmChattingWith._id };
        console.log(newMessage);
        // console.log(userWhomAmChattingWith._id);
        // console.log(loggedInUser._id);
    }

    return (
        <div>
            {userWhomAmChattingWith ? <>
                <ul className="list-inline text-center mx-1">
                    <li><UserProfilePreview user={userWhomAmChattingWith} /></li>
                    {Array(100).map((num) => (
                        <li className="text-left">
                            <p className="mx-2">
                                <span className="p-2 bg-light rounded mr-2">@{userWhomAmChattingWith ? userWhomAmChattingWith.username : 'username'}</span> messagewefwfwefe text....
                        </p>
                        </li>
                    ))}

                    {/* <li className="text-right">
                        <p className="mx-2">
                            messagewefwfwefe text.... <span className="p-2 bg-secondary text-light rounded ml-2">@me</span>
                        </p>
                    </li> */}
                </ul>
                <footer style={{ position: 'absolute', bottom: '20px', left: '20px', right: '0' }} className="text-center p-0">
                    <form onSubmit={formOnSubmit} className="form-row w-100 text-center m-0">
                        <input onChange={event => setMessageText(event.target.value)} type="text" className={`${styles.messageInput} col-7 p-3 mx-2 rounded`} placeholder="Type in your message here" />
                        {messageText.length > 0 ? <input type="submit" className="btn btn-dark px-3 mx-2 col-2" value="=> Send" /> : <input type="submit" className="btn btn-dark px-3 mx-2 col-2" value="=> Send" disabled />}
                        {messageText.length > 0 ? <button className="btn btn-success mx-2 col-2">Now</button> : <button className="btn btn-success mx-2 col-2" disabled>Now</button>}
                    </form>
                </footer>
            </> : <div>
                    <p className="text-secondary m-5">- Chazzier!. :) -</p>
                </div>}
        </div>
    );
}

const Dashboard = () => {
    const [loggedInUser, setLoggedInUser] = useState(null);
    const [userChatWith, setUserChatWith] = useState(null);
    const [otherUsers, setOtherUsers] = useState([]);

    useEffect(() => {
        axios.get('http://localhost:8000/api/users', { withCredentials: true })
            .then(res => {
                setLoggedInUser(res.data.loggedInUser);
                setOtherUsers(res.data.otherUsers);
            })
            .catch(err => navigate('/welcome'));
    }, []);

    const logOut = () => {
        axios.get('http://localhost:8000/api/users/logout', { withCredentials: true })
            .then(res => navigate('/'))
            .catch(err => console.log(err));
    }

    return (
        <div>
            <Navbar className={`p-3`} expand="lg">
                <h3 className="p-0 m-0" href="#">Chazzy.</h3>
                <Navbar.Toggle aria-controls="nav-collapse" />
                <Navbar.Collapse id="nav-collapse" className="ml-4">
                    <Nav className="mr-auto align-middle">
                        <Nav.Link className="align-middle mx-1" href="#">Hi, {loggedInUser ? loggedInUser.firstName : ''}!</Nav.Link>
                        <Nav.Link className="align-middle mx-1" href="#">Recents</Nav.Link>
                    </Nav>
                    <Nav className="ml-auto m-0 p-0">
                        <Nav.Link onClick={logOut} className="align-middle mx-1" href="#">Log Out.</Nav.Link>
                    </Nav>
                </Navbar.Collapse>
            </Navbar>
            <div className={`${styles.rowHeight} container-fluid m-0 p-0`}>
                <Row className="h-100 w-100">
                    <Col style={{ backgroundImage: 'linear-gradient(white, rgb(245, 245, 245))' }} className="h-100" md={5}>
                        <Row className="m-0 p-0 py-3">
                            <ul className="list-inline p-0 m-0" style={{ overflowX: 'scroll' }}>
                                {otherUsers.map(otherUser => (
                                    <li key={otherUser._id} className="list-inline-item mx-2">
                                        <UserProfilePreview onClick={() => setUserChatWith(otherUser)} user={otherUser} />
                                    </li>
                                ))}
                            </ul>
                        </Row>
                        <Row className="p-3">
                            <ul className="list-inline w-100">
                                <li><h5 className="mx-auto mb-3">Messages</h5></li>
                                <li className="w-100 text-left">
                                    <ul className="list-inline">
                                        <li>
                                            <ul className="list-inline m-3">
                                                <li className="list-inline-item align-middle">
                                                    <img width="70" height="70" className="rounded-circle" src={Grey} alt="" /><br />
                                                </li>
                                                <li className="list-inline-item align-middle text-left mx-2">
                                                    <h6 className="m-0 font-weight-bold">@username</h6>
                                                    <p className="m-0"><small>Latest message</small></p>
                                                </li>
                                                <li className="list-inline-item float-right text-right">
                                                    <h6 className="m-0 py-4 font-weight-bold text-secondary">3 mins ago</h6>
                                                </li>
                                            </ul>
                                            <ul className="list-inline m-3">
                                                <li className="list-inline-item align-middle">
                                                    <img width="75" height="75" className="rounded-circle" src={Grey} alt="" /><br />
                                                </li>
                                                <li className="list-inline-item align-middle text-left mx-2">
                                                    <h6 className="m-0 font-weight-bold">@username</h6>
                                                    <p className="m-0"><small>Latest message</small></p>
                                                </li>
                                                <li className="list-inline-item float-right text-right">
                                                    <h6 className="m-0 py-4 font-weight-bold text-secondary">3 mins ago</h6>
                                                </li>
                                            </ul>
                                        </li>
                                    </ul>
                                </li>
                            </ul>
                        </Row>
                    </Col>
                    <Col className="p-3 h-100" md={7}>
                        <Context.Provider value={{ loggedInUser }}>
                            <ChatArea userWhomAmChattingWith={userChatWith} />
                        </Context.Provider>
                    </Col>
                </Row>
            </div>
        </div>
    )
}

export default Dashboard;
