import React, { useState, useEffect } from 'react';
import { navigate } from '@reach/router';
import axios from 'axios';
import Grey from '../grey.jpg';

// Bootstrap
import { Navbar, Nav, Row, Col } from 'react-bootstrap';
import styles from './Dashboard.module.css';

const Dashboard = () => {
    const [loggedInUser, setLoggedInUser] = useState(null);
    const [otherUsers, setOtherUsers] = useState([]);

    useEffect(() => {
        axios.get('http://localhost:8000/api/users', { withCredentials: true })
            .then(res => {
                setOtherUsers(res.data.users);
                setLoggedInUser(res.data.loggedInUser);
            })
            .catch(err => navigate('/welcome'));
    }, []);

    const logOut = () => {
        axios.get('http://localhost:8000/api/users/logout', { withCredentials: true })
            .then(res => navigate('/'))
            .catch(err => console.log(err));
    }

    return (
        <div>
            {/* <Row>
                <Navbar className={`p-3 ${styles.navbar}`} expand="lg">
                    <h3 className="p-0 m-0" href="#">Chazzy.</h3>
                    <Navbar.Toggle aria-controls="nav-collapse" />
                    <Navbar.Collapse id="nav-collapse" className="ml-4">
                        <Nav className="mr-auto align-middle">
                            <Nav.Link className="align-middle mx-1" href="#">Hi, {loggedInUser ? loggedInUser.firstName : ''}!</Nav.Link>
                            <Nav.Link className="align-middle mx-1" href="#">Recents</Nav.Link>
                        </Nav>
                        <Nav className="ml-auto m-0 p-0">
                            <Nav.Link onClick={logOut} className="align-middle mx-1" href="#">Log Out.</Nav.Link>
                        </Nav>
                    </Navbar.Collapse>
                </Navbar>
            </Row> */}
            <div className="container-fluid p-0">

                <Row>
                    <Col className="heightFull bg-light p-3" md={5}>
                        <Row className="m-0 p-0 py-3">
                            <ul className="list-inline pb-3 m-0" style={{ overflowY: 'hidden', overflowX: 'scroll' }}>
                                {otherUsers.map(otherUser => (
                                    <li key={otherUser._id} className="list-inline-item mx-3">
                                        <img width="100" height="100" className="rounded-circle" src={Grey} alt="" /><br />
                                        <h6 className="mt-2">@{otherUser.username}</h6>
                                    </li>
                                ))}
                            </ul>
                        </Row>
                        <Row className="p-3">
                            <ul className="list-inline w-100">
                                <li>
                                    <h3 className="mx-auto">Messages</h3>
                                </li>
                                <li className="w-100 text-left">
                                    <ul className="list-inline">
                                        <li>
                                            <ul className="list-inline m-0">
                                                <li className="list-inline-item align-middle">
                                                    <img width="75" height="75" className="rounded-circle" src={Grey} alt="" /><br />
                                                </li>
                                                <li className="list-inline-item align-middle text-left mx-2">
                                                    <h6 className="m-0 font-weight-bold">@username</h6>
                                                    <p className="m-0"><small>Latest message</small></p>
                                                </li>
                                                <li className="list-inline-item float-right text-right">
                                                    <h6 className="m-0 font-weight-bold">3 mins ago</h6>
                                                </li>
                                            </ul>
                                        </li>
                                    </ul>
                                </li>
                            </ul>
                        </Row>
                    </Col>
                    <Col className="heightFull" md={7}>
                    </Col>
                </Row>
            </div>
        </div>
    )
}

export default Dashboard;
